import React, { useContext } from "react";
import { CartContext } from "./context/CartContext";

const Cart = () => {
  const { cart, updateCartQuantity, removeFromCart } = useContext(CartContext);

  // Calculate total price per item
  const calculateTotalPrice = (item) => {
    return item.price && item.quantity ? (item.price * item.quantity).toFixed(2) : "0.00";
  };

  // Calculate cart total
  const totalCartPrice = cart.reduce((total, item) => total + (item.price * item.quantity || 0), 0);

  // Apply 10% discount
  const finalPrice = (totalCartPrice * 0.9).toFixed(2);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Shopping Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cart.map((item) => (
            <div key={item.id} className="border p-4 mb-2 shadow-md rounded-lg flex items-center justify-between">
              <div>
                <h3 className="font-bold">{item.title}</h3>
                <p className="text-gray-700">Price: ${item.price?.toFixed(2)}</p>
                <p className="text-gray-900 font-bold">
                  Total: ${calculateTotalPrice(item)}
                </p>
              </div>

              {/* Quantity Input */}
              <input
                type="number"
                min="0"
                value={item.quantity}
                onChange={(e) => {
                  const newQuantity = parseInt(e.target.value, 10);
                  if (newQuantity > 0) {
                    updateCartQuantity(item.id, newQuantity);
                  } else {
                    removeFromCart(item.id); // Remove if 0
                  }
                }}
                className="w-16 border rounded p-1 text-center"
              />
            </div>
          ))}
          <h2 className="text-xl font-bold mt-4">Total Price: ${totalCartPrice.toFixed(2)}</h2>
          <h2 className="text-xl font-bold text-green-600">Final Price after 10% Discount: ${finalPrice}</h2>
        </>
      )}
    </div>
  );
};

export default Cart;
