import { useContext } from "react";
import { CartContext } from "./context/CartContext";

const Cart = () => {
  const { cart, increaseQuantity, decreaseQuantity, removeFromCart, totalPrice, finalPrice } = useContext(CartContext);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Shopping Cart</h2>
      {cart.length === 0 ? (
        <p className="text-gray-600">Your cart is empty.</p>
      ) : (
        <div>
          {cart.map((item) => (
            <div key={item.id} className="flex items-center justify-between border-b py-2">
              <div className="flex items-center">
                <img src={item.image} alt={item.title} className="h-16 w-16 object-cover" />
                <div className="ml-4">
                  <h3 className="font-semibold">{item.title}</h3>
                  <p className="text-gray-600">${item.price.toFixed(2)} x {item.quantity}</p>
                  <p className="text-gray-800 font-bold">Total: ${(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </div>
              <div className="flex items-center">
                <button onClick={() => decreaseQuantity(item.id)} className="bg-gray-300 px-2 py-1 rounded mr-2">-</button>
                <span className="text-lg">{item.quantity}</span>
                <button onClick={() => increaseQuantity(item.id)} className="bg-gray-300 px-2 py-1 rounded ml-2">+</button>
                <button onClick={() => removeFromCart(item.id)} className="ml-4 bg-red-500 text-white px-4 py-2 rounded">Remove</button>
              </div>
            </div>
          ))}
          <div className="mt-4 p-4 border-t">
            <h3 className="text-lg font-semibold">Subtotal: ${totalPrice.toFixed(2)}</h3>
            <h3 className="text-lg font-semibold text-green-600">Discounted Total (10% off): ${finalPrice.toFixed(2)}</h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
